import React, { useState, createContext  } from "react";
import PageWrapper from "../layout/page-wrapper/page-wrapper";
import { GlobalStyle } from "./styles";
import { ThemeProvider } from "styled-components";
import { defaultTheme, lightTheme, darkTheme, defaultFont, secondaryFont } from "../../thems/default";

export const ThemeContext = createContext(null)

function App() {
  const [theme, setTheme] = useState("light");
  
  const switchTheme = () => {
    theme === "light" ? setTheme("dark") : setTheme("light");
  };

  const [font, setFont] = useState("default");
  
  const switchFont = () => {
    font === "default" ? setFont("secondary") : setFont("default");
  };

  const fontTheme = font === "default" ? defaultFont : secondaryFont;
  const colorTheme = theme === "light" ? darkTheme : lightTheme;

  return (
    <>
      <ThemeProvider theme={{...defaultTheme, ...fontTheme, ...colorTheme}}>
        <GlobalStyle />
        <PageWrapper />
      </ThemeProvider>
    </>
  );
}

export default App;
